import React, { Component } from 'react';
import EmployeeService from '../services/EmployeeService';
import { Link } from 'react-router-dom'
import { useParams } from 'react-router-dom';

export function withRouter(UpdateEmployeeComponent) {
    return (props) => {
        const match = { params: useParams() };
        return <UpdateEmployeeComponent {...props} match={match} />
    }
}

class UpdateEmployeeComponent extends Component {
    constructor(props){
        super(props);

        let {id} = props.params;
        this.state = {
            id: id,
            firstName: '',
            lastName: '',
            jobRole: '',
            salary: 0.0,
            totalShortages: 0.0,
            totalSalary: 0.0,

        }
        this.changeFirstNameHandler = this.changeFirstNameHandler.bind(this);
        this.changeLastNameHandler = this.changeLastNameHandler.bind(this);
        this.updateEmployee = this.updateEmployee.bind(this);
    }

    componentDidMount(){
        EmployeeService.getEmployeeById(this.state.id).then((res) => {
            let employee = res.data;

           this.setState({
            firstName: employee.firstName,
            LastName: employee.LastName,
            jobRole: employee.jobRole,
            salary: employee.salary,
            totalShortages: employee.totalShortages,
            totalSalary: employee.totalSalary,
           })

        });
    }


    updateEmployee = (e) => {
        e.preventDefault();
        let employee = {firstName: this.state.firstName, lastName: this.state.lastName, jobRole: this.state.jobRole, salary: this.state.salary, totalShortages: this.state.totalShortages, totalSalary: this.state.totalSalary};
        console.log('employees => ' + JSON.stringify(employee));

    }
    changeFirstNameHandler = (event) => {
        this.setState({firstName: event.target.value});
    }
    changeLastNameHandler = (event) => {
        this.setState({lastName: event.target.value});
    }
    changeJobRoleHandler = (event) => {
        this.setState({jobRole: event.target.value});
    }

    //COLLECTS THE VALUE INTO SALARY
    changeSalaryHandler = (event) => {
        const salary = parseFloat(event.target.value);
    this.setState({ salary }, () => {
      this.updateTotalSalary();
    });
    }

        //COLLECTS THE VALUE INTO TOTAL SHORTAGES
    changeTotalShortagesHandler = (event) => {
        const totalShortages = parseFloat(event.target.value);
    this.setState({ totalShortages }, () => {
      this.updateTotalSalary();
    });
    }

    //METHOD TO DO THE SUBSTRACTION
    updateTotalSalary = () => {
        const { salary, totalShortages } = this.state;

        const totalSalary = salary - totalShortages;
        this.setState({ totalSalary });
      };



      //JSX
    render() {
        return (
            <div className="container" style={{marginTop: "100px"}}>
                <div className="row">
                    <div className="card col-md-6 offset-md-3 offset-md-3">
                        <h3 className="text-center">Add Employee</h3>
                        <div className="card-body">

                <form className="row g-3 needs-validation">
                    <div className="col-md-4">
                        <label>First name</label>
                        <input type="text" name="firstName" className="form-control" value={this.state.firstName} onChange={this.changeFirstNameHandler}required/>
                            <div className="valid-feedback">
                                Looks good!
                            </div>
                    </div>
                    <div className="col-md-4">
                        <label>Last name</label>
                        <input type="text" name="lastName" className="form-control" value={this.state.lastName} onChange={this.changeLastNameHandler}required/>
                            <div className="valid-feedback">
                                Looks good!
                            </div>
                    </div>
                    <div className="col-md-4">
                        <label>Job Role</label>
                        <input type="text" name="jobRole" className="form-control" value={this.state.jobRole} onChange={this.changeJobRoleHandler}required/>
                            <div className="valid-feedback">
                                Looks good!
                            </div>
                    </div>
                    <div className="col-md-4">
                        <label>Salary</label>
                        <div className="input-group has-validation">
                            <span className="input-group-text" id="inputGroupPrepend">₦</span>
                            <input type="number" name="salary" className="form-control" value={this.state.salary} onChange={this.changeSalaryHandler}required/>
                                <div className="invalid-feedback">
                                    Please input the salary.
                                </div>
                        </div>
                    </div>
                    <div className="col-md-4">
                        <label>Total Shortage</label>
                        <div className="input-group has-validation">
                            <span className="input-group-text" id="inputGroupPrepend">₦</span>
                            <input type="number" name="salary" className="form-control" value={this.state.totalShortages} onChange={this.changeTotalShortagesHandler}required/>
                                <div className="invalid-feedback">
                                    Please input the Total Shortage.
                                </div>
                        </div>
                    </div>
                    <div className="col-md-4">
                        <label>Total Salary</label>
                        <div className="input-group has-validation">
                            <span className="input-group-text" id="inputGroupPrepend">₦</span>
                            <input type="number" name="totalSalary" className="form-control" value={this.state.totalSalary} onChange={this.changeTotalSalaryHandler}disabled/>
                                <div className="invalid-feedback">
                                    Please input the Total Salary.
                                </div>
                        </div>
                    </div>


                    <div className="col-12">
                        <button className="btn btn-primary" onClick={this.updateEmployee}>Update</button>
                        <Link to="/employees"><button className="btn btn-danger" style={{marginLeft: "10px"}}>Cancel</button></Link>
                    </div>
                </form>
                </div>
                </div>
            </div>
            </div>
        );
    }
}

export default withRouter(UpdateEmployeeComponent);
