import React, { Component } from 'react';
import EmployeeService from '../services/EmployeeService';
import { Link } from 'react-router-dom'



class ListEmployeeComponent extends Component {
    constructor(props){
        super(props);


        this.state = {
            employees:[]
        }

        this.editEmployee = this.editEmployee.bind(this);
    }
    componentDidMount(){
        EmployeeService.getEmployees().then((res) => {
            this.setState({employees:res.data});
        });
    }

    editEmployee(id){

        window.location.replace(`/update-employee/${id}`)

    };


    render() {
        return (
            <div style={{marginTop: "30px"}}>
                <h2 className="text-center">Employee's List</h2>

                <div className="row">
                    <table className="table table-striped table-bordered">
                        <thead>
                            <th>First Name</th>
                            <th>Last Name</th>
                            <th>Job Role</th>
                            <th>Salary</th>
                            <th>Total Shortages</th>
                            <th>Total Salary</th>
                            <th>Actions</th>
                        </thead>
                        <tbody>
                            { 
                              this.state.employees.map(
                                employees=>
                            <tr key={employees.id}>
                                <td>{employees.firstName}</td>
                                <td>{employees.lastName}</td>
                                <td>{employees.jobRole}</td>
                                <td>{employees.salary}</td>
                                <td>{employees.totalShortage}</td>
                                <td>{employees.totalSalary}</td>
                                <td>
                                <button type="button" onClick={() => this.editEmployee(employees.id)} className="btn btn-info">Update</button>
                                <Link to="/delete-employee"><button type="button" style={{marginLeft: "15px"}} onClick={() => this.employees.id} className="btn btn-danger">Delete</button></Link>

                                </td>
                            </tr>
                            )
                            }
                        </tbody>
                    </table>
                </div>
            </div>
        );
    }
}

export default ListEmployeeComponent;
